function showdown(n,t){var i=n+","+t;window.checklogin(showDownAfterLogin,[i])}function showDownAfterLogin(n){var t=n.split(",")[0],i=n.split(",")[1];$.post("/Qikan/Article/GetArticleRight",{articleId:t},function(n){var u=n.RetValue,r;u?getdowninfo(t,i):(r="/",window.open(r,"_blank"))})}function getdowninfo(n){var t="";$.ajax({url:"/Qikan/Article/IsLocalDown",type:"post",async:!1,data:{id:n},success:function(i){if(t=i.RetValue.localPath,t=="noLocal")layer.alert("本地全文缺失");else{var r=$("<form><\/form>").attr("action","/Qikan/Article/ArticleLocalDown").attr("method","post");r.append($("<input><\/input>").attr("type","hidden").attr("name","path").attr("value",t));r.append($("<input><\/input>").attr("type","hidden").attr("name","id").attr("value",n));r.appendTo("body").submit().remove();layer.msg("下载中",{icon:16,shade:.01,time:3e3})}}})}function showOnLineRead(n,t){var i="";$.ajax({url:"/Qikan/Article/IsLocalDown",type:"post",async:!1,data:{id:n},success:function(r){if(i=r.RetValue.localPath,i=="noLocal")layer.alert("本地全文缺失");else{var u=n+","+t;window.checklogin(showOnLineReadAfterLogin,[u])}}})}function showOnLineReadAfterLogin(n){var t=n.split(",")[0],i=n.split(",")[1];$.ajax({url:"/Qikan/Article/GetArticleRight",type:"post",async:!1,data:{articleId:t},success:function(n){var r=n.RetValue;r&&getOnLineRead(t,i)}})}function getOnLineRead(n,t){var i="/Qikan/Article/ReadIndex?id="+n+"&info="+t;window.open(i,"_blank")}